mapper.update:
update eas_student
name = #{name}, sex = #{sex}, birthday = #{birthday}, phone = #{phone}, class_id = #{class_id}, motto = #{motto}
where username=#{username}

mapper.select:
SELECT s.id,u.username,s.name,s.sex,s.birthday,s.phone,c.classes,s.motto FROM ( eas_student as s inner join eas_class as c on s.class_id = c.id) inner join eas_user as u on s.username = u.username
and name like CONCAT('%',#{name,jdbcType=VARCHAR},'%')
and phone like CONCAT('%',#{phone,jdbcType=VARCHAR},'%')
and class_id = #{class_id}
order by id ASC;

select count(*)
from eas_student as A, eas_score as B, eas_course as C, eas_base_course as BC,eas_class as CS

    A.id = B.s_id and C.id = B.c_id and C.base_course_id = BC.id and CS.id = A.class_id
    -- 未结束课程 complete = 0 --
    and C.complete = 0
    and C.t_id = #{tId}

        and C.base_course_id = #{baseCourseId}


        and A.class_id = #{classId}

select count(*)
from eas_student as A, eas_score as B, eas_course as C, eas_base_course as BC,eas_class as CS

    A.id = B.s_id and C.id = B.c_id and C.base_course_id = BC.id and CS.id = A.class_id
    -- 已结束课程 complete = 1 --
    and C.complete = 1
    and C.t_id = #{tId}

        and C.base_course_id = #{baseCourseId}


        and A.class_id = #{classId}

select B.id as id, A.id as sId, A.name as sName, sex, phone,
score, result, C.id as cId, BC.coursename as courseName,CS.classes as className
from eas_student as A , eas_score as B , eas_course as C , eas_base_course as BC,eas_class as CS

    A.id = B.s_id and C.id = B.c_id and C.base_course_id = BC.id and A.class_id = CS.id
    and C.complete = 1
    and C.t_id = #{tId}

        and C.base_course_id = #{baseCourseId}


        and A.class_id = #{classId}



    limit #{pageUtil.pageStart},#{pageUtil.count}

select B.id as id, A.id as sId, A.name as sName, sex, phone,
    C.id as cId, BC.coursename as courseName,CS.classes as className
from eas_student as A , eas_score as B , eas_course as C , eas_base_course as BC,eas_class as CS

    A.id = B.s_id and C.id = B.c_id and C.base_course_id = BC.id and A.class_id = CS.id
    and C.complete = 0
    and C.t_id = #{tId}

        and C.base_course_id = #{baseCourseId}


        and A.class_id = #{classId}



    limit #{pageUtil.pageStart},#{pageUtil.count}
